package ru.itmo.person_service;import io.github.cdimascio.dotenv.Dotenv;import lombok.extern.slf4j.Slf4j;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;@Slf4j@SpringBootApplicationpublic class PersonServiceApplication {	public static void main(String[] args) {		Dotenv dotenv = Dotenv.configure()				.directory("../")				.filename(".env")				.load();		dotenv.entries().forEach(entry ->				System.setProperty(entry.getKey(), entry.getValue())		);		SpringApplication.run(PersonServiceApplication.class, args);	}}